import React from 'react';
import './App.css';
import Button from './Components/button';
class App extends React.Component{
    state={value:"",todo:[]};
    a=123;
    todo=[];
    saveTodo=()=>{
        const {value,todo}=this.state;
        console.log(value);
        todo.push(value);
        this.setState({todo:todo});
        console.log(todo);
    };
    delete=(i)=>{
        return()=>
        {
            const {todo}=this.state;
            todo.splice(i,1);
            this.setState({});
        }

    };
    handleInput=(e)=>
    {
        const value=e.target.value;
        this.value=value;
        this.setState({value:value});
    };
    render(){
        const{value:a,todo}=this.state;
        return(
            <div>
                <h1>To Do List</h1>
                <input value={this.state.a}
                       onChange={this.handleInput}/>

                <button onClick={this.saveTodo}>SAVE</button>
                <ul>
                    {
                        todo.map((item,i)=>{
                            return(
                                <div><li key={i}>{item}</li>
                                    <button onClick={this.delete(i)}>DELETE</button>
                                </div>
                            )
                        })
                    }
                </ul>
            </div>
        )

    }


}

export default App;
